import React from 'react';
import  {Route } from 'react-router-dom'
import CollectionsOverview from '../../components/collection-overview/collection-overview.components'
import CollectionPage from '../collection/collection.component'
import { firestore, convertCollectionsSnapshot} from '../../firebase/firebase.utils'
import {connect} from 'react-redux'
import { updateCollections } from '../../redux/shop/shop.actions';


class ShopPage extends React.Component{
        unsubscribeFromSnapshot = null;
        componentDidMount() {
        const {updateCollections} = this.props;
        const collectionRef = firestore.collection('collections')
        
                this.unsubscribeFromSnapshot = collectionRef.onSnapshot(
                        async (snapshot) => {
                                const collectionsMap = convertCollectionsSnapshot(snapshot);
                                updateCollections(collectionsMap)

        })
        }

        render() {
        const {match} = this.props;
        return(
                (<div className='shop-page'>
                        <Route exact path={`${match.path}`} component={CollectionsOverview} />
                        <Route path={`${match.path}/:collectionId`} component={CollectionPage} />
                </div>

))}} 
const mapDispatchToProps = dispatch => ({updateCollection: collectionsMap => dispatch(updateCollections(collectionsMap))})



export default connect(null, mapDispatchToProps)(ShopPage);
